      <Tabs 
        items={tabItems} 
        defaultActiveTab="notifications"
        variant="pills"
      />
    </div>
  );
};

export default TabsExample;

"use client";

import React, { useState } from "react";
import { ColorPicker } from "../../../../index";

const ColorPickerExample: React.FC = () => {
  const [primaryColor, setPrimaryColor] = useState("#0066cc");
  const [secondaryColor, setSecondaryColor] = useState("#ff6b6b");

  const handlePrimaryColorChange = (color: string) => {
    console.log("Primary color changed:", color);
    setPrimaryColor(color);
  };

  const handleSecondaryColorChange = (color: string) => {
    console.log("Secondary color changed:", color);
    setSecondaryColor(color);
  };

  // Override the preset colors by modifying the PRESET_COLORS array
  const CUSTOM_COLORS = ["#ff6b6b", "#4ecdc4", "#45b7d1", "#96ceb4", "#ffeaa7"];

  return (
    <div className="space-y-8 p-4">
      {/* Basic Usage */}
      <div className="space-y-4">
        <h2 className="ihub-fs-lg">Basic Color Picker</h2>
        <ColorPicker
          id="basic-color-picker"
          label="Basic Color Picker"
          defaultColor="#000000"
          onChange={(color) => console.log("Basic color changed:", color)}
        />
      </div>

      {/* Without Dropdown Button */}
      <div className="space-y-4">
        <h2 className="ihub-fs-lg">Without Dropdown Button</h2>
        <ColorPicker
          showButton={false}
          defaultColor="#4ecdc4"
          onChange={(color) => console.log("No button color changed:", color)}
        />
      </div>

      {/* Form Integration */}
      <div className="space-y-4">
        <h2 className="ihub-fs-lg">Form Integration</h2>
        <form className="space-y-4">
          <div>
            <ColorPicker
              label="Primary Color"
              name="primary_color"
              defaultColor={primaryColor}
              onChange={handlePrimaryColorChange}
              className="ihub-mb-3"
            />
          </div>
          <div>
            <ColorPicker
              label="Secondary Color"
              name="secondary_color"
              defaultColor={secondaryColor}
              onChange={handleSecondaryColorChange}
              className="ihub-mb-3"
            />
          </div>
          <div>
            <ColorPicker
              label="Hide Preview, Formats, and EyeDropper"
              className="ihub-mb-3"
              hidePreview={true}
              hideFormats={true}
              hideEyeDropper={true}
            />
          </div>
          <button
            type="submit"
            className="px-4 py-2 bg-blue-500 text-white rounded"
          >
            Save Colors
          </button>
        </form>
      </div>

      {/* Controlled Component Example */}
      <div className="space-y-4">
        <h2 className="ihub-fs-lg">Controlled Component</h2>
        <ColorPicker
          defaultColor={primaryColor}
          onChange={handlePrimaryColorChange}
        />
        <div
          style={{
            width: "100px",
            height: "100px",
            backgroundColor: primaryColor,
            marginTop: "20px",
            borderRadius: "8px",
          }}
        />
      </div>

      {/* Custom Preset Colors */}
      <div className="space-y-4">
        <h2 className="ihub-fs-lg">Custom Preset Colors</h2>
        <ColorPicker
          CUSTOM_COLORS={CUSTOM_COLORS}
          onChange={(color) => console.log("Custom color changed:", color)}
        />
      </div>
    </div>
  );
};

export default ColorPickerExample;

"use client";
import { ContentViewer, ContentViewOrEdit } from "../../../../index";
import { useState } from "react";

console.log(ContentViewer);

export default function ContentViewOrEditSample() {
  const [content, setContent] = useState<string>(
    "<h2>Getting Started with InstinctHub</h2><p>Welcome to your course content! This editor allows you to create rich, interactive content for your students.</p><ul><li>Format text using the toolbar above</li><li>Add images, tables, and code samples</li><li>Create interactive task lists</li></ul><p>Click the edit button to make changes, then preview to see how your content will appear to students.</p>"
  );
  const [isEditing, setIsEditing] = useState(false);

  const toggleEditMode = () => {
    setIsEditing(!isEditing);
  };
  return (
    <div>
      <ContentViewer
        content={content}
        title="Course Module: Introduction"
        onContentChange={toggleEditMode}
        showToolbar={true}
      />

      <ContentViewOrEdit
        setContent={setContent}
        content={content}
        title="Course Module: Introduction"
        showToolbar={true}
        placeholder="Write your course content here..."
        charLimit={10000}
        lastUpdated="2023-05-01 12:00:00"
        showEditBtn={true}
        showPreviewBtn={true}
      />
    </div>
  );
}

"use client"
import React, { useState } from "react";
import { Dialog } from "../../../../index";

const DialogExample: React.FC = () => {
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  const openDialog = () => {
    setIsDialogOpen(true);
  };

  const closeDialog = () => {
    setIsDialogOpen(false);
  };

  return (
    <div className="ihub-container">
      <h1>Dialog Example</h1>
      
      <button 
        className="ihub-important-btn" 
        onClick={openDialog}
      >
        Open Dialog
      </button>
      
      <Dialog
        isOpen={isDialogOpen}
        onClose={closeDialog}
        title="Confirmation"
        footer={
          <div className="ihub-buttons">
            <button 
              className="ihub-danger-btn"
              onClick={closeDialog}
            >
              Cancel
            </button>
            <button 
              className="ihub-important-btn"
              onClick={() => {
                // Handle confirmation
                closeDialog();
              }}
            >
              Confirm
            </button>
          </div>
        }
      >
        <p>Are you sure you want to proceed with this action?</p>
      </Dialog>
    </div>
  );
};

export default DialogExample;
